SELECT COUNT(*) FROM u_merchant um
LEFT JOIN u_user uu ON um.user_id = uu.id AND uu.`status` > 0
LEFT JOIN u_qrcode_info uqi ON um.user_id = uqi.user_id AND uqi.`status` > 0
LEFT JOIN u_merchant_application ma ON um.id = ma.merchant_id AND ma.type = 1 and ma.`status` > 0


        AND um.state = #{state}
        AND um.state IN (2)
        AND um.state IN(-1,1) 


        AND um.`entity_type`=#{entityType}


        AND um.`main_business`=#{mainBusiness}


        AND uu.`email` LIKE CONCAT('%', #{email}, '%')


        AND uqi.`code` =#{code}


        AND um.`corporate_name` LIKE CONCAT('%',#{corporateName},'%')


        AND um.`practical_name` LIKE CONCAT('%',#{practicalName},'%')


        AND um.`modified_date` BETWEEN #{start} AND #{end}


        AND um.`is_available`=#{isAvailable}


        AND um.`city`=${city}


        AND um.status = #{status}
        AND um.status > 0

SELECT um.*,  um.email  as liaisonEmail ,uqi.code as code,(case when uu.email is null or uu.email = '' then ma.email else  uu.email end ) as accountEmail
FROM u_merchant um
LEFT JOIN u_user uu ON um.user_id = uu.id AND uu.`status` > 0
LEFT JOIN u_qrcode_info uqi ON um.user_id = uqi.user_id AND uqi.`status` > 0
LEFT JOIN u_merchant_application ma ON um.id = ma.merchant_id AND ma.type = 1 and ma.`status` > 0


        AND um.`id`=#{id}


        AND um.state = #{state}
        AND um.state IN (2)
        AND um.state IN(-1,1,3,4) 


        AND um.`entity_type`=#{entityType}


        AND um.`main_business`=#{mainBusiness}


        AND uu.`email` LIKE CONCAT('%', #{email}, '%')


        AND uqi.`code` =#{code}


        AND um.`corporate_name` LIKE CONCAT('%',#{corporateName},'%')


        AND um.`practical_name` LIKE CONCAT('%',#{practicalName},'%')


        AND um.`modified_date` BETWEEN #{start} AND #{end}


        AND um.`is_available`=#{isAvailable}


        AND um.`city`=${city}


        AND um.status = #{status}
        AND um.status > 0



    ORDER BY

        um.${sc.field} ${sc.order}



    LIMIT ${pc.startIndex}, ${pc.pageSize}

