        SELECT um.id, (case when uu.email is null or uu.email = '' then ma.email else  uu.email end ) as email,um.corporate_name,um.practical_name,um.entity_type,um.main_business,um.is_available,um.created_date, um.modified_date,um.is_top,
        um.longitude_latitude,um.asic_extract_file_name,um.asic_extract_file_url,um.pay_sell_discount,um.pay_rebate_discount,um.marketing_discount,
        um.base_rate,um.app_charge_rate,um.extra_discount,um.extra_discount_period,um.modified_date,um.state,um.contract_type,um.city
        FROM u_merchant um
        LEFT JOIN u_user uu ON um.user_id = uu.id AND uu.`status` > 0
        LEFT JOIN u_merchant_application ma ON um.id = ma.merchant_id AND ma.type = 1 and ma.`status` > 0
--         LEFT JOIN u_qrcode_info uqi ON um.user_id = uqi.user_id AND uqi.`status` > 0
