SELECT
 *
FROM u_merchant


        AND `main_business` = 3


        AND `main_business` IN (2, 3)


        AND `main_business` IN (1, 3)


        AND `id`=#{id}


        AND `ARBN`=#{arbn}


        AND `user_id`=#{userId}


        AND `entity_type`=#{entityType}


        AND `company_type`=#{companyType}


        AND `asic_company_type`=#{asicCompanyType}


        AND `foreign_company_type`=#{foreignCompanyType}


        AND `is_foreign`=#{isForeign}


        AND `is_foreign_registered`=#{isForeignRegistered}


        AND `is_available`=#{isAvailable}


        AND `ABN`=#{abn}


        AND `ACN`=#{acn}


        AND `corporate_name` LIKE CONCAT('%', #{corporateName},'%')


        AND `practical_name` LIKE CONCAT('%', #{practicalName}, '%')


        AND `registered_name`=#{registeredName}


        AND `phone`=#{phone}


        AND `email`=#{email}


        AND `postcode`=#{postcode}


        AND `BSB`=#{bsb}


        AND `is_top`= 1


        AND `remark`=#{remark}


        AND `logo_url`=#{logoUrl}


        AND `intro`=#{intro}


        AND `created_by`=#{createdBy}


        AND `created_date` BETWEEN #{start} AND #{ end}


        AND `modified_by`=#{modifiedBy}


        AND `modified_date`=#{modifiedDate}


        AND `state`=#{state}


        And status = #{status}
        And status > 0


        AND `keyword`=#{keyword}




        ORDER BY

            ${sc.field} ${sc.order}




    LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT * FROM u_merchant WHERE status > 0 AND is_top = 1
ORDER BY top_sort ASC

    LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT * FROM u_merchant

    WHERE top_sort < (
    SELECT top_sort FROM u_merchant WHERE id = #{id}
    ) AND is_top = 1 AND status > 0 ORDER BY top_sort DESC


    WHERE top_sort > (
    SELECT top_sort FROM u_merchant WHERE id = #{id}
    ) AND is_top = 1 AND status > 0 ORDER BY top_sort ASC

LIMIT 0, 1

SELECT COUNT(*) FROM u_merchant um
LEFT JOIN u_user uu ON um.user_id = uu.id AND uu.`status` > 0
LEFT JOIN u_qrcode_info uqi ON um.user_id = uqi.user_id AND uqi.`status` > 0


        AND um.`state` IN (5, 6, 7, 8, 1)


        AND um.`state`=#{approveState}


        AND um.`id`=#{id}


        AND um.`entity_type`=#{entityType}


        AND um.`main_business`=#{mainBusiness}


        AND uu.`email` LIKE CONCAT('%', #{email}, '%')


        AND uqi.`code` =#{code}


        AND um.`corporate_name` LIKE CONCAT('%',#{corporateName},'%')


        AND um.`practical_name` LIKE CONCAT('%',#{practicalName},'%')


        AND um.`contract_start_time` BETWEEN #{css} AND #{cse}


        AND um.`contract_end_time` BETWEEN #{ces} AND #{cee}


        AND um.`is_available`=#{isAvailable}


        AND um.`city`=${city}


        AND um.status = #{status}
        AND um.status > 0

SELECT um.*, um.email as liaisonEmail ,uqi.code as code, uu.email as accountEmail, COUNT(cr.id) contractFilesCount
FROM u_merchant um
LEFT JOIN u_user uu ON um.user_id = uu.id AND uu.`status` > 0
LEFT JOIN u_qrcode_info uqi ON um.user_id = uqi.user_id AND uqi.`status` > 0

LEFT JOIN u_merchant_contract_file_record cr on um.id  = cr.merchant_id AND cr.`status` > 0


        AND um.`state` IN (5, 6, 7, 8, 1)


        AND um.`state`=#{approveState}


        AND um.`id`=#{id}


        AND um.`entity_type`=#{entityType}


        AND um.`main_business`=#{mainBusiness}


        AND uu.`email` LIKE CONCAT('%', #{email}, '%')


        AND uqi.`code` =#{code}


        AND um.`corporate_name` LIKE CONCAT('%',#{corporateName},'%')


        AND um.`practical_name` LIKE CONCAT('%',#{practicalName},'%')


        AND um.`contract_start_time` BETWEEN #{css} AND #{cse}


        AND um.`contract_end_time` BETWEEN #{ces} AND #{cee}


        AND um.`is_available`=#{isAvailable}


        AND um.`city`=${city}


        AND um.status = #{status}
        AND um.status > 0


GROUP BY um.id

    ORDER BY

        um.${sc.field} ${sc.order}



    LIMIT ${pc.startIndex}, ${pc.pageSize}

