FROM u_merchant um
LEFT JOIN u_user uu ON um.user_id = uu.id AND uu.`status` > 0
LEFT JOIN u_qrcode_info uqi ON um.user_id = uqi.user_id AND uqi.`status` > 0


        AND um.`id`=#{id}


        AND um.account_apply_state = #{accountApplyState}
        AND um.account_apply_state IN (3)
        AND um.account_apply_state IN(1,3,4) 


        AND um.`entity_type`=#{entityType}


        AND um.`main_business`=#{mainBusiness}


        AND uu.`email` LIKE CONCAT('%', #{email}, '%')


        AND uqi.`code` =#{code}


        AND um.`corporate_name` LIKE CONCAT('%',#{corporateName},'%')


        AND um.`modified_date` BETWEEN #{start} AND #{end}


        AND um.`is_available`=#{isAvailable}


        AND um.status = #{status}
        AND um.status > 0



    ORDER BY

        um.${sc.field} ${sc.order}



    LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT * FROM u_merchant
where `status`= 1

and `docusign_has_signed` = 1
and `is_top`= 1
and `is_available` = 1

    and   `city` = #{city}


    and  `city` != #{city}

order by `merchant_approve_pass_time` DESC
LIMIT #{limit};

select * from u_merchant where `status`= 1 and state not in (-1,0,2)

    and   `practical_name` like  CONCAT('%', #{practicalName}, '%')

 order by practical_name asc  LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT mm.id AS id, mm.mname AS practical_name, mm.state AS state, mm.abn AS abn, mm.city FROM ( SELECT f.merchant_id AS id, m.practical_name mname, m.state state, m.abn, m.city FROM u_qr_pay_flow f LEFT JOIN u_merchant m ON f.merchant_id = m.id WHERE f.`status` = 1 AND m.id IS NOT NULL AND f.state IN (1, 31) AND f.is_need_clear = 1 AND f.sale_type in (0,2)
AND m.`city`=${city}
AND f.rec_amount > 0
AND f.`clear_state` = #{clearState}
AND m.`practical_name` LIKE CONCAT('%', #{practicalName}, '%')
AND m.ABN LIKE CONCAT('%', #{abn}, '%')
AND f.`created_date` BETWEEN #{start} AND #{ end}
GROUP BY f.merchant_id ) mm ORDER BY mm.mname ASC
LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT mm.id AS id, mm.mname AS practical_name, mm.state AS state, mm.abn AS abn, mm.user_id AS user_id, mm.city FROM ( SELECT w.merchant_id AS id, m.practical_name mname, m.state state, m.abn abn, m.user_id user_id, m.city FROM u_whole_sales_flow w LEFT JOIN u_merchant m ON w.merchant_id = m.id WHERE w.`status` = 1 AND m.id IS NOT NULL AND w.state = 1
AND m.`practical_name` LIKE CONCAT('%', #{practicalName}, '%')
AND m.ABN LIKE CONCAT('%', #{abn}, '%')
AND w.settlement_state = #{settlementState}
AND w.approve_state = #{approveState}
AND w.settlement_delay = #{settlementDelay}
AND w.pass_time BETWEEN #{start} AND #{end}
AND m.city=#{city}
GROUP BY w.merchant_id ) mm ORDER BY mm.mname ASC
LIMIT ${pc.startIndex}, ${pc.pageSize}

select max(created_date) from u_merchant u where u.is_available=1 and status=1
AND u.`practical_name` LIKE CONCAT('%', #{searchKeyword}, '%')
AND u.`created_date` > #{endTime}
ORDER BY u.`practical_name` ASC
LIMIT ${pc.startIndex}, ${pc.pageSize}

select m.id,m.is_available,m.practical_name,m.email,m.status,m.user_id
from u_merchant m
where id=${id}

SELECT u.practical_name practicalName, u.id, u.logo_url logoUrl FROM u_merchant u WHERE u.is_available = 1 AND STATUS = 1 AND state = 1
AND u.`practical_name` LIKE CONCAT(#{keywords}, '%')
AND u.`categories` = #{categoryType}
AND u.`merchant_state` = #{stateName}
ORDER BY u.`practical_name` ASC

SELECT u.id, u.practical_name practicalName FROM u_merchant u WHERE u.is_available = 1 AND STATUS = 1
AND u.`ABN` = #{abn}
ORDER BY u.`created_date` DESC

SELECT m.id as merchantId, m.categories, m.have_whole_sell as haveWholeSell, m.whole_sale_user_pay_discount as wholeSaleUserPayDiscount, m.whole_sale_user_discount as wholeSaleUserDiscount, m.is_available as isAvailable, m.practical_name as tradingName, m.keyword, m.lng, m.lat, m.marketing_discount as marketingDiscount, m.extra_discount_period as extraDiscountPeriod, m.base_pay_rate as basePayRate, m.extra_discount as extraDiscount, m.base_rate as baseRate, m.logo_url as logoUrl, m.city, IFNULL(f.`status`,0) as isFavorite FROM u_merchant m
left join u_favorite_merchant f on f.user_id = #{userId} and m.id = f.merchant_id and f.`status`=1
where m.is_available = 1 and m.docusign_has_signed = 1 and m.`categories` is not null and m.`status`= 1 and m.`state` = 1
and m.`merchant_state` = #{merchantState}
and m.`keyword` LIKE CONCAT('%', #{keyword}, '%')
and m.`categories` = #{categories}
and ( (m.`lng` is not null and m.`lng` != '') AND (m.`lat` is not null and m.`lat` != '') )
AND ( m.`practical_name` LIKE CONCAT('%', #{searchKeyword}, '%') OR m.`keyword` LIKE CONCAT('%', #{searchKeyword}, '%')
OR m.`categories` in (${categoriesIn})
)
AND convert(m.lat, decimal(15,8)) >= #{locationRange.latMin} AND convert(m.lat, decimal(15,8)) <= #{locationRange.latMax} AND convert(m.lng, decimal(15,8)) >= #{locationRange.lngMin} AND convert(m.lng, decimal(15,8)) <= #{locationRange.lngMax}
order by m.`merchant_approve_pass_time` DESC

SELECT count(distinct m.`id`) FROM u_merchant m where m.is_available = 1 and m.docusign_has_signed = 1 and m.`categories` is not null and m.`status`= 1 and m.`state` = 1
and ( (m.`lng` is not null and m.`lng` != '') AND (m.`lat` is not null and m.`lat` != '') )
and m.`keyword` LIKE CONCAT('%', #{keyword}, '%')
and m.`categories` = #{categories}
AND ( m.`practical_name` LIKE CONCAT('%', #{searchKeyword}, '%') OR m.`keyword` LIKE CONCAT('%', #{searchKeyword}, '%')
OR m.`categories` in (${categoriesIn})
)
AND convert(m.lat, decimal(15,8)) >= #{locationRange.latMin} AND convert(m.lat, decimal(15,8)) <= #{locationRange.latMax} AND convert(m.lng, decimal(15,8)) >= #{locationRange.lngMin} AND convert(m.lng, decimal(15,8)) <= #{locationRange.lngMax}
