SELECT a.id,a.trans_amount,a.pay_amount,a.created_date,b.corporate_name,c.phone, a.trans_type as trans_type FROM u_qr_pay_flow a LEFT JOIN u_withhold_flow w ON a.id = w.flow_id LEFT JOIN u_merchant b ON a.rec_user_id=b.user_id and b.`status` >0 LEFT JOIN u_user c ON a.pay_user_id=c.id and c.`status` >0
AND a.`state`='31' and a.status > 0 AND a.`clear_state` = 0 AND a.`is_need_clear`=1 AND w.check_state IN (1, 2)
