AND a.`rec_user_id`=#{recUserId}
AND a.`clear_state`=#{clearState}
AND a.`trans_type`=#{transType}
AND a.`created_date` BETWEEN #{start} AND #{ end}
union all SELECT r.flow_id as id, r.refund_amount as trans_amount, r.refund_amount-r.not_settlement_amount as pay_amount, r.created_date as created_date, mer.corporate_name as corporate_name, puser.phone, r.trans_type as trans_type FROM u_refund_flow r LEFT JOIN u_qr_pay_flow qr ON qr.id = r.flow_id LEFT JOIN u_user puser ON qr.pay_user_id = puser.id LEFT JOIN u_merchant mer ON mer.user_id = r.org_rec_user_id WHERE r. STATUS > 0 AND (r.state = 2 AND r.settlement_state=3 ) AND r.gateway_id = #{gatewayId} AND r.org_rec_user_id =#{userId}
AND r.`created_date` BETWEEN #{start} AND #{ end}


