AND re.`created_date` BETWEEN #{start} AND #{ end}
UNION ALL select g.gateway_type as payType,q.trans_amount,q.created_date,q.fee, (case when fee_direction = 0 then 0 else q.rate end) as rate ,q.pay_amount,'1' as showType from u_qr_pay_flow q left join u_gateway g on g.type=q.gateway_id where q.pay_user_id=#{userId} and q.state in (21,31)
AND q.`created_date` BETWEEN #{start} AND #{ end}
UNION ALL select g.gateway_type as payType,q.trans_amount,q.created_date,q.fee,q.rate,q.pay_amount,'2' as showType from u_qr_pay_flow q left join u_gateway g on g.type=q.gateway_id where q.rec_user_id=#{userId} and q.state in (21,31)
AND q.`created_date` BETWEEN #{start} AND #{ end} )kk order by kk.created_date desc

