mapper.select:
SELECT count(*) FROM (select * from ( SELECT p.rec_amount AS amt, p.rec_user_id AS userId FROM u_qr_pay_flow p LEFT JOIN u_withhold_flow w ON p.id = w.flow_id WHERE p. STATUS > 0 AND p.state = 31 AND p.gateway_id = #{gatewayId} AND p.is_need_clear = 1 AND w.check_state IN (1, 2) AND p.clear_state = 0
AND p.`created_date` BETWEEN #{start} AND #{ end}
AND p.`rec_user_id` =#{userId}
) su GROUP BY su.userId) gp

SELECT
#{gatewayId} as gateway_id , (case when amt <= 0 then 0 else amt end ) AS recAmountTotal, con AS count, userId AS rec_user_id, m.corporate_name, m.BSB, m.account_no, m.bank_name, transAmt as transAmountTotal
FROM ( SELECT count(*) AS con, sum(su.amt) AS amt, su.userId AS userId, sum(su.transAmt) as transAmt FROM ( ( SELECT p.rec_amount AS amt, p.rec_user_id AS userId, p.trans_amount as transAmt FROM u_qr_pay_flow p LEFT JOIN u_withhold_flow w ON p.id = w.flow_id WHERE p. STATUS > 0 AND p.state = 31 AND p.gateway_id = #{gatewayId} AND is_need_clear = 1 AND p.clear_state = 0 AND w.check_state IN (1, 2)
AND p.`created_date` BETWEEN #{start} AND #{ end}
AND p.`rec_user_id` =#{userId}
) UNION ALL ( SELECT ( CASE WHEN r.settlement_state = 3 THEN r.refund_amount - r.not_settlement_amount ELSE - r.not_settlement_amount END ) AS amt, r.org_rec_user_id AS userId, r.refund_amount as transAmt FROM u_refund_flow r WHERE r. STATUS > 0 AND r.state = 2 AND r.settlement_state=3 AND r.gateway_id = #{gatewayId}
AND r.`org_rec_user_id` =#{userId}
) ) su GROUP BY su.userId ) kk LEFT JOIN u_merchant m ON kk.userId = m.user_id

SELECT
    count(*)
FROM
    (
        SELECT
            mer.platform_id
        FROM
            u_qr_pay_flow qr
        LEFT JOIN u_access_merchant mer ON qr.merchant_id = mer.id

              qr.access_party_server_fee > 0
                AND qr. STATUS > 0

                AND qr.`state`=#{state}


                AND mer.`platform_id`=#{platformId}


                AND qr.`acc_plt_fee_clear_state`=#{orgAccPltFeeClearState}



                    #{orderSource}



                AND qr.`created_date` BETWEEN #{start} AND #{ end}


        GROUP BY
           mer.platform_id
    ) i

