qr.access_party_server_fee > 0 AND qr. STATUS > 0
AND qr.`state`=#{state}
AND mer.`platform_id`=#{platformId}
AND qr.`acc_plt_fee_clear_state`=#{orgAccPltFeeClearState}
#{orderSource}
AND qr.`created_date` BETWEEN #{start} AND #{ end}
GROUP BY mer.platform_id, mer.account_name, mer.account_no, mer.bsb, mer.name

