            #{orderSource}



        AND qr.`created_date` BETWEEN #{start} AND #{ end}

SELECT COUNT(DISTINCT pay.id) FROM u_qr_pay_flow pay WHERE pay.status = 1 AND (pay.state = 1 OR pay.state = 31) AND pay.merchant_id = #{merchantId} AND pay.sale_type IN (1, 2)
AND pay.trans_no LIKE CONCAT('%', #{orderNo},'%')
AND pay.`created_date` BETWEEN #{start} AND #{ end}

SELECT pay.trans_no orderNo, pay.whole_sales_amount transAmount, pay.whole_sales_discount orderWholeSalesDiscount, FROM_UNIXTIME(pay.created_date/1000,'%h:%i %d-%m-%Y') createdDate, pay.whole_sales_discount wholeSalesDiscount, pay.credit_order_no creditOrderNo, pay.sale_type saleType, pay.trans_type transType FROM u_qr_pay_flow pay WHERE pay.status = 1 AND (pay.state = 1 OR pay.state = 31) AND pay.merchant_id = #{merchantId} AND pay.sale_type IN (1, 2)
AND pay.trans_no LIKE CONCAT('%', #{orderNo},'%')
AND pay.`created_date` BETWEEN #{start} AND #{ end}
ORDER BY pay.created_date DESC
LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT
    *
FROM
    (
        SELECT
            qr.trans_no,
            qr.trans_amount,
            qr.clear_amount,
            qr.created_date,
            qr.sale_type,
            qr.clear_state,
            qr.id
        FROM
            u_qr_pay_flow qr

        qr.sale_type != 1

        AND `pay_user_id`=#{payUserId}


        AND `pay_user_type`=#{payUserType}


        AND `pay_account_type`=#{payAccountType}


        AND `rec_user_id`=#{recUserId}


        AND `rec_user_type`=#{recUserType}


        AND `rec_account_type`=#{recAccountType}


        AND `merchant_id`=#{merchantId}


        AND `is_need_clear`=#{isNeedClear}


        AND `clear_state`=#{clearState}


        AND `trans_type`=#{transType}


        AND `created_date` BETWEEN #{start} AND #{ end}


        AND `credit_order_no`=#{creditOrderNo}


        AND `trans_no`=#{transNo}


        AND `state` in

            #{state}



        AND `sale_type` in (1,2)


        AND `sale_type` in (0,2)


        AND `trans_no` like concat('%',#{queryTransNo},'%')


        And status = #{status}
        And status > 0



        AND `clear_time` BETWEEN #{clearStart} AND #{ clearEnd}


        UNION ALL
            SELECT
                ws.id AS trans_no,
                amount as trans_amount,
                ws.settlement_amount AS clear_amount,
                ws.pass_time as created_date,
                1 as sale_type,
                ws.settlement_state as clear_state,
                ws.id as id
            FROM
                u_whole_sales_flow ws


        AND `merchant_id`=#{merchantId}


        AND `trans_type`=#{transType}


        AND `state`=#{state}


        AND `settlement_time`=#{settlementTime}


        AND `settlement_time` BETWEEN #{clearStart} AND #{ clearEnd}


        AND `settlement_state`=#{settlementState}


        AND `pass_time` BETWEEN #{start} AND #{ end}


        AND `modified_date` BETWEEN #{modifiedStart} AND #{ modifiedEnd}


        AND `pass_time` BETWEEN #{passTimeStart} AND #{ passTimeEnd}


        AND `approve_state`=#{approveState}


        AND `order_type`=#{orderType}


        AND status = #{status}
        AND status > 0


        AND `check_state`=#{checkState}


        AND `check_time`=#{checkTime}


        AND `id` like concat('%',#{queryTransNo},'%')


        ) al
        ORDER BY  al.created_date desc

        LIMIT ${pc.startIndex}, ${pc.pageSize}

select count(*) from (
select (case when trans_type in  ('0', '2', '4') then 0 else 1 end) as  payType,trans_amount,created_date,charge as fee,trans_amount+charge as payAmount,'0' as showType from u_recharge_flow
where user_id =#{userId} and state in (21,31)

    AND `created_date` BETWEEN #{start} AND #{ end}

UNION ALL
select (case when trans_type in  ('0', '2', '4') then 0 else 1 end) as  payType,trans_amount,created_date,fee,pay_amount,'1' as showType from u_qr_pay_flow where pay_user_id=#{userId} and
state in (21,31)

    AND `created_date` BETWEEN #{start} AND #{ end}

UNION ALL
select (case when trans_type in  ('0', '2', '4') then 0 else 1 end) as  payType,trans_amount,created_date,fee,pay_amount,'2' as showType from u_qr_pay_flow where rec_user_id=#{userId} and
state in (21,31)

    AND `created_date` BETWEEN #{start} AND #{ end}

) kk

SELECT COUNT(id) FROM u_qr_pay_flow
WHERE `status` = 1
AND pay_user_id = #{userId}


    AND `trans_type`=#{transType}


    AND `trans_type` IN (2,22)


    AND `created_date` BETWEEN #{start} AND #{ end}

SELECT q.*, FROM_UNIXTIME(q.created_date/1000,'%M,%Y') as displayDate, FROM_UNIXTIME(q.created_date/1000,'%M,%Y') as monthYear, FROM_UNIXTIME(q.created_date/1000,'%m-%Y') as monthYearStr, IFNULL(m.`logo_url`,'') as logo_url, IFNULL(m.`practical_name`,'') as trading_name, q.`state` as `trans_state`, s.`en_name` as `transTypeStr`
FROM u_qr_pay_flow q
LEFT JOIN `u_merchant` m ON q.`merchant_id` = m.`id` LEFT JOIN `u_static_data` s ON q.`trans_type` = s.`value` and s.code = 'transType' and s.`status`=1 WHERE q.`status` = 1 AND q.`is_show` = 1 AND q.pay_user_id = #{userId}
AND q.`trans_type`=#{transType}
AND q.`trans_type` IN (2,22)
AND q.`created_date` BETWEEN #{start} AND #{ end}
order by q.`created_date` desc
LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT COUNT(DISTINCT q.merchant_id)
FROM u_qr_pay_flow q left join u_merchant m on q.merchant_id=m.id
WHERE q.status > 0
AND (q.state = 1 OR q.state = 31)

    AND q.created_date BETWEEN #{start} AND #{end}


    AND m.city=#{city}

SELECT t1.merchant_id, IFNULL(t2.practical_name, '') AS merchantName, t1.rec_user_id AS recUserId,
SUM(CASE WHEN t1.sale_type = 0 THEN 1 ELSE 0 END) AS basicSalesOrderQuantity, SUM(CASE WHEN t1.sale_type = 0 THEN t1.trans_amount ELSE 0 END) AS basicSalesOrderAmount, SUM(CASE WHEN t1.sale_type = 1 THEN 1 ELSE 0 END) AS wholeSalesOrderQuantity, SUM(CASE WHEN t1.sale_type = 1 THEN t1.trans_amount ELSE 0 END) AS wholeSalesOrderAmount, SUM(CASE WHEN t1.sale_type = 2 THEN 1 ELSE 0 END) AS mixSalesOrderQuantity, SUM(CASE WHEN t1.sale_type = 2 THEN t1.trans_amount ELSE 0 END) AS mixSalesOrderAmount, SUM(t1.red_envelope_amount) AS redEnvelopeAmount,
SUM(CASE WHEN t1.sale_type = 0 AND t1.trans_type = 2 THEN t1.trans_amount ELSE 0 END) AS basicSalesOrderAmountByCard, SUM(CASE WHEN t1.sale_type = 0 AND t1.trans_type = 2 THEN t1.pay_amount ELSE 0 END) AS basicSalesCustomerActualPaymentAmountByCard, SUM(CASE WHEN t1.sale_type = 0 AND t1.trans_type = 2 THEN t1.rec_amount ELSE 0 END) AS basicSalesNeedSettledAmountByCard, SUM(CASE WHEN t1.sale_type = 0 AND t1.trans_type = 22 THEN t1.trans_amount ELSE 0 END) AS basicSalesOrderAmountByInstalment, SUM(CASE WHEN t1.sale_type = 0 AND t1.trans_type = 22 THEN t1.pay_amount ELSE 0 END) AS basicSalesCustomerActualPaymentAmountByInstalment, SUM(CASE WHEN t1.sale_type = 0 AND t1.trans_type = 22 THEN t1.rec_amount ELSE 0 END) AS basicSalesNeedSettledAmountByInstalment,
SUM(CASE WHEN t1.sale_type = 1 AND t1.trans_type = 2 THEN t1.trans_amount ELSE 0 END) AS wholeSalesOrderAmountByCard, SUM(CASE WHEN t1.sale_type = 1 AND t1.trans_type = 2 THEN t1.pay_amount ELSE 0 END) AS wholeSalesCustomerActualPaymentAmountByCard, SUM(CASE WHEN t1.sale_type = 1 AND t1.trans_type = 22 THEN t1.trans_amount ELSE 0 END) AS wholeSalesOrderAmountByInstalment, SUM(CASE WHEN t1.sale_type = 1 AND t1.trans_type = 22 THEN t1.pay_amount ELSE 0 END) AS wholeSalesCustomerActualPaymentAmountByInstalment,
SUM(CASE WHEN t1.sale_type = 2 AND t1.trans_type = 2 THEN t1.trans_amount ELSE 0 END) AS mixSalesOrderAmountByCard, SUM(CASE WHEN t1.sale_type = 2 AND t1.trans_type = 2 THEN t1.pay_amount ELSE 0 END) AS mixSalesCustomerActualPaymentAmountByCard, SUM(CASE WHEN t1.sale_type = 2 AND t1.trans_type = 2 THEN t1.rec_amount ELSE 0 END) AS mixSalesNeedSettledAmountByCard, SUM(CASE WHEN t1.sale_type = 2 AND t1.trans_type = 22 THEN t1.trans_amount ELSE 0 END) AS mixSalesOrderAmountByInstalment, SUM(CASE WHEN t1.sale_type = 2 AND t1.trans_type = 22 THEN t1.pay_amount ELSE 0 END) AS mixSalesCustomerActualPaymentAmountByInstalment, SUM(CASE WHEN t1.sale_type = 2 AND t1.trans_type = 22 THEN t1.rec_amount ELSE 0 END) AS mixSalesNeedSettledAmountByInstalment, SUM(t1.whole_sales_amount) AS todayMerchantWholeSalesAmount, t2.city FROM u_qr_pay_flow t1 LEFT JOIN u_merchant t2 ON t1.merchant_id = t2.id AND t1.status = t2.status WHERE t1.status > 0 AND (t1.state = 1 OR t1.state = 31)
AND t1.created_date BETWEEN #{start} AND #{end}
AND t2.city=#{city}
AND FROM_UNIXTIME(t1.`created_date`/1000,'%Y-%m-%d') = #{today}
GROUP BY t1.merchant_id ORDER BY merchantName ASC
LIMIT ${pc.startIndex}, ${pc.pageSize}

