SELECT count(*) FROM ( SELECT f.merchant_id AS id FROM u_qr_pay_flow f LEFT JOIN u_merchant m ON f.merchant_id = m.id WHERE f.`status` = 1 AND m.`status` = 1 AND f.state IN (1, 31) AND f.is_need_clear = 1 AND f.sale_type in (0,2) AND f.order_source = 0
AND m.`city`=${city}
