SELECT pay_user_id, sum(pay_amount) as pay_amount, sum(trans_amount) as trans_amount, sum( case when trans_amount - pay_amount > 0 then trans_amount - pay_amount else 0 end ) as firstAmount, sum(fee) as fee
FROM u_qr_pay_flow q WHERE STATUS = 1 AND state IN (1, 31) AND created_date BETWEEN #{start} AND #{end}
AND pay_user_id = #{payUserId}
GROUP BY pay_user_id;

SELECT COUNT(distinct id) FROM u_qr_pay_flow
WHERE `status` = 1
and `is_show` = 1
AND pay_user_id = #{userId}


    AND `trans_type`=#{transType}


    AND `trans_type` IN (2,22)


    AND `created_date` BETWEEN #{start} AND #{ end}


    AND `trans_amount` between #{amtStart} AND #{amtEnd}


    AND `created_date` between #{lastDate} and ${now}

SELECT q.id, q.trans_type as transType, q.`state` as transState, q.`pay_amount` as payAmount, q.`pay_amount` + q.`donation_amount` + q.`tip_amount` as totalAmount, q.`trans_amount` as transAmount, q.`merchant_id` as merchantId, q.`created_date` as createdDate, FROM_UNIXTIME(q.created_date/1000,'%a %d %b %Y') as displayDate, IFNULL(m.`logo_url`,'') as logoUrl, ( CASE WHEN q.order_source = 0 THEN IFNULL( m.`practical_name`, '' ) WHEN q.order_source = 1 THEN IFNULL( am.`practical_name`, '' ) ELSE '' END ) AS tradingName, s.`en_name` as `transTypeStr` FROM u_qr_pay_flow q
LEFT JOIN `u_merchant` m ON q.`merchant_id` = m.`id` LEFT JOIN `u_api_merchant` am ON q.`merchant_id` = am.`id`
LEFT JOIN `u_static_data` s ON q.`trans_type` = s.`value` and s.code = 'transType' and s.`status`=1 WHERE q.`status` = 1 AND q.`is_show` = 1 AND q.pay_user_id = #{userId}
AND q.`trans_type`=#{transType}
AND q.`trans_type` IN (2,22)
AND q.`created_date` BETWEEN #{start} AND #{end}
AND q.`trans_amount` between #{amtStart} AND #{amtEnd}
AND q.`created_date` between #{lastDate} and ${now}
ORDER BY
${sc.field} ${sc.order}
ORDER BY q.id DESC
LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT count(*) FROM ( SELECT m.id AS id FROM u_api_merchant m LEFT JOIN u_qr_pay_flow f ON f.merchant_id = m.id AND f.order_source = 1 WHERE f.`status` = 1 AND m.`status` = 1 AND f.state = 31 AND f.is_need_clear = 1 AND f.sale_type in (0,2)
AND m.`city`=${city}
AND f.rec_amount > 0 AND m.id IS NOT NULL
AND f.`clear_state` = #{clearState}
AND m.`practical_name` LIKE CONCAT('%', #{practicalName}, '%')
AND m.`id` = #{merchantId}
AND m.ABN = #{abn}
AND f.`created_date` BETWEEN #{start} AND #{ end}
GROUP BY m.id ) m

SELECT
    count(*)
FROM
    u_qr_pay_flow


        AND `pay_user_id`=#{userId}


        AND `trans_type`=#{transType}


        AND `credit_order_no`=#{borrowId}


        AND `id`= #{flowId}


        AND `marketing_id`= #{markingId} and state in(1,31)


        AND status = #{status}
        AND status > 0

SELECT f.trans_no AS transNo, f.whole_sales_discount_amount wholeDiscountAmount, (f.base_discount_amount + f.extra_discount_amount + f.marking_discount_amount) normalDiscountAmount, f.order_source orderSource, (CASE WHEN f.trans_type = 22 THEN m2.merchant_name ELSE m.practical_name END) AS merchantName, f.trans_amount AS orderAmount, ( f.whole_sales_discount_amount + f.base_discount_amount + f.extra_discount_amount + f.marking_discount_amount ) AS discountAmount, f.whole_sales_discount AS wholeSalesDiscount, f.base_discount AS baseDiscount, f.extra_discount AS extraDiscount, f.marking_discount AS markingDiscount, f.red_envelope_amount AS payoMoney, (f.pay_amount + wf.charge) as creditTotalAmt, (f.trans_amount - f.red_envelope_amount - f.whole_sales_discount_amount - f.base_discount_amount - f.extra_discount_amount - f.marking_discount_amount + f.fee) as cardTotalAmt, f.donation_amount AS donationAmount, f.tip_amount AS tipAmount, ( CASE WHEN f.trans_type = 22 THEN wf.charge WHEN f.trans_type = 2 THEN f.fee ELSE 0 END ) fee, ( CASE WHEN f.trans_type = 22 THEN wf.fee_rate WHEN f.trans_type = 2 THEN f.rate ELSE 0 END ) rate, ( f.pay_amount + f.tip_amount + f.donation_amount ) AS totalAmount, f.state, f.card_id AS cardId, f.created_date as paidTime, wf.card_no AS cardNo, wf.card_cc_type as cardCcType, wf.error_message AS errorMessage, wf.trans_amount as creditFirstPayAmt, wf.order_amount firstPeriodAmt, b.period_quantity allPeriod, f.credit_order_no borrowId, f.trans_type transType FROM u_qr_pay_flow f LEFT JOIN u_merchant m ON m.id = f.merchant_id LEFT JOIN u_withhold_flow wf ON wf.flow_id = f.id LEFT JOIN uwallet_credit.u_borrow b ON b.id = f.credit_order_no LEFT JOIN uwallet_credit.u_merchant m2 ON m2.id = f.merchant_id
AND f.`pay_user_id`=#{userId}
AND f.`trans_type`=#{transType}
AND f.`created_date` BETWEEN #{start} AND #{end}
AND f.`credit_order_no`=#{borrowId}
AND f.`id`= #{flowId}
AND f.`marketing_id`= #{markingId} and f.state in(1,31)
AND f.status = #{status} AND f.status > 0
ORDER BY f.created_date desc
LIMIT ${pc.startIndex}, ${pc.pageSize}

