SELECT m.amount, f.pay_user_id, m.created_date, uu.user_last_name lastName, uu.user_first_name firstName FROM `u_marketing_flow` m LEFT JOIN u_user u ON u.id = m.user_id LEFT JOIN u_qr_pay_flow f ON f.id = m.flow_id left join u_user uu on uu.id=f.pay_user_id WHERE m.user_id = #{id}
and m.created_date BETWEEN #{start} AND #{ end}
AND m.status = 1 AND m.marketing_manage_id is not null AND m.direction = 0 AND m.trans_type = 20 AND m.state = 1 ORDER BY m.created_date

SELECT * FROM ( SELECT ctd.id, ctd.transaction_amount AS repayAmount, ctd.transaction_charge AS charge, ctd.charge_rate AS chargeRate, ctd.transaction_type AS repaymentType, ctd.transaction_status AS repaymentStatus, rr.pay_type AS payType, rr.bankcard_number AS bankCardNumber, rr.bankcard_code cardId, pwf.error_message, pwf.card_cc_type AS cardCcType, FROM_UNIXTIME( ctd.created_date / 1000, '%H:%i:%s %d/%m/%y' ) AS repaymentTimeStr, ctd.created_date AS repaymentTime FROM uwallet_credit.u_repay_record rr LEFT JOIN uwallet_credit.u_transaction_detail ctd ON rr.transaction_id = ctd.id LEFT JOIN uwallet_credit.u_borrow b ON b.id = rr.borrow_id LEFT JOIN uwallet_credit.u_repay rp ON rp.id = rr.repay_id LEFT JOIN uwallet_pay.u_withhold_flow pwf ON pwf.flow_id = ctd.id WHERE b.user_id = #{userId} AND rp.period_sort != 1 AND rr.`status` = 1 AND b.`status` = 1 AND ctd.`status` = 1 AND pwf.`status` = 1
AND ctd.`created_date` BETWEEN #{start} AND #{end}
GROUP BY ctd.id
union all SELECT ctd.id, ctd.transaction_amount AS repayAmount, ctd.transaction_charge AS charge, ctd.charge_rate AS chargeRate, ctd.transaction_type AS repaymentType, ctd.transaction_status AS repaymentStatus, cof.pay_type AS payType, cof.bankcard_number AS bankCardNumber, cof.bankcard_code cardId, pwf.error_message, pwf.card_cc_type AS cardCcType, FROM_UNIXTIME( ctd.created_date / 1000, '%H:%i:%s %d/%m/%y' ) AS repaymentTimeStr, cof.created_date AS repaymentTime FROM uwallet_credit.u_overdue_fee_record cof LEFT JOIN uwallet_credit.u_transaction_detail ctd ON ctd.id = cof.transaction_id LEFT JOIN uwallet_credit.u_overdue_fee cov on cov.id=cof.overdue_fee_id LEFT JOIN uwallet_pay.u_withhold_flow pwf ON pwf.flow_id = ctd.id WHERE cov.user_id=#{userId}
AND cof.`created_date` BETWEEN #{start} AND #{end}
) t1 GROUP BY id

SELECT * FROM ( SELECT ctd.id, ctd.transaction_amount AS repayAmount, ctd.transaction_charge AS charge, ctd.charge_rate AS chargeRate, ctd.transaction_type AS repaymentType, ctd.transaction_status AS repaymentStatus, rr.pay_type AS payType, rr.bankcard_number AS bankCardNumber, rr.bankcard_code cardId, pwf.error_message, pwf.card_cc_type AS cardCcType, FROM_UNIXTIME( ctd.created_date / 1000, '%H:%i:%s %d/%m/%y' ) AS repaymentTimeStr, ctd.created_date AS repaymentTime FROM uwallet_credit.u_repay_record rr LEFT JOIN uwallet_credit.u_transaction_detail ctd ON rr.transaction_id = ctd.id LEFT JOIN uwallet_credit.u_borrow b ON b.id = rr.borrow_id LEFT JOIN uwallet_credit.u_repay rp ON rp.id = rr.repay_id LEFT JOIN uwallet_pay.u_withhold_flow pwf ON pwf.flow_id = ctd.id WHERE b.user_id = #{userId} AND rp.period_sort != 1 AND rr.`status` = 1 AND b.`status` = 1 AND ctd.`status` = 1 AND pwf.`status` = 1
AND ctd.`created_date` BETWEEN #{start} AND #{end}
GROUP BY ctd.id union all SELECT ctd.id, ctd.transaction_amount AS repayAmount, ctd.transaction_charge AS charge, ctd.charge_rate AS chargeRate, ctd.transaction_type AS repaymentType, ctd.transaction_status AS repaymentStatus, cof.pay_type AS payType, cof.bankcard_number AS bankCardNumber, cof.bankcard_code cardId, pwf.error_message, pwf.card_cc_type AS cardCcType, FROM_UNIXTIME( ctd.created_date / 1000, '%H:%i:%s %d/%m/%y' ) AS repaymentTimeStr, cof.created_date AS repaymentTime FROM uwallet_credit.u_overdue_fee_record cof LEFT JOIN uwallet_credit.u_transaction_detail ctd ON ctd.id = cof.transaction_id LEFT JOIN uwallet_credit.u_overdue_fee cov on cov.id=cof.overdue_fee_id LEFT JOIN uwallet_pay.u_withhold_flow pwf ON pwf.flow_id = ctd.id WHERE cov.user_id=#{userId}
AND cof.`created_date` BETWEEN #{start} AND #{end}
) t1 GROUP BY id ORDER BY repaymentTime DESC
LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT count(*) FROM ( SELECT * FROM ( SELECT cr.expect_repay_time FROM uwallet_credit.u_repay cr LEFT JOIN uwallet_credit.u_borrow b ON cr.borrow_id = b.id WHERE b.user_id = #{userId} AND cr.`status` = 1 AND b.`status` = 1 AND cr.state != 1
AND cr.`expect_repay_time` BETWEEN #{start} AND #{end}
GROUP BY cr.expect_repay_time UNION ALL SELECT expect_repay_time FROM uwallet_credit.u_overdue_fee WHERE user_id = #{userId} AND `status` = 1 AND state != 1
AND `expect_repay_time` BETWEEN #{start} AND #{end}
GROUP BY expect_repay_time ) t1 GROUP BY t1.expect_repay_time ) t2

SELECT * FROM ( SELECT cr.expect_repay_time FROM uwallet_credit.u_repay cr LEFT JOIN uwallet_credit.u_borrow b ON cr.borrow_id = b.id WHERE b.user_id = #{userId} AND cr.`status` = 1 AND b.`status` = 1 AND cr.state not in (1,3,4) AND b.user_id = #{userId}
AND cr.`expect_repay_time` BETWEEN #{start} AND #{end}
GROUP BY cr.expect_repay_time UNION ALL SELECT expect_repay_time FROM uwallet_credit.u_overdue_fee WHERE user_id = #{userId} AND `status` = 1 AND state != 1
AND `expect_repay_time` BETWEEN #{start} AND #{end}
GROUP BY expect_repay_time ) t1 GROUP BY t1.expect_repay_time
LIMIT ${pc.startIndex}, ${pc.pageSize}

SELECT id, email, user_first_name, user_last_name, created_date, push_token FROM u_user WHERE status = 1 AND created_date BETWEEN #{start} AND #{end}
AND `first_deal_state` = #{firstDealState}
AND `push_token` is not null AND `push_token` != ''
AND id = #{userId}
ORDER BY created_date desc

mapper.update:
UPDATE u_user u
SET u.split_red_envelope_state = #{state}
WHERE u.id  = #{id}
and u.split_red_envelope_state = #{orgState}

UPDATE uwallet_pay.u_marketing_management
SET receive_number = #{number}
WHERE
    id  = #{id}
